require 'spec_helper'

describe Spree::Api::ProductsController do
  render_views

  let(:user) { create(:user, email: 'spree@example.com') }
  let(:taxon_1) { create(:taxon) }
  let(:taxon_2) { create(:taxon) }

  describe '#create' do
    context 'when user is admin' do
      before do
        user.spree_roles.create(name: 'admin')
        stub_authentication!
        controller.stub(:try_spree_current_user).and_return user
        create(:stock_location)
      end

      context 'when params are correct' do
        let(:product_params) do
          {
              product: {
                  name:              'Foo',
                  description:       'Foo Product',
                  sku:               'foo001',
                  price:             0.0,
                  shipping_category: 'Default',
                  taxon_ids: [taxon_1.id, taxon_2.id].join(','),
                  stock:             10,
                  images:            [
                                         {
                                             base64:       '',
                                             file_name:    'test.png',
                                             content_type: 'image/png'
                                         }
                                     ]
              }
          }
        end

        it {
          expect {
            api_post :create, product_params
          }.to change { Spree::Product.count }.by 1
        }

        it 'response code is 201' do
          api_post :create, product_params
          expect(response.code).to eql '201'
        end

        it 'has 10 items in stock' do
          api_post :create, product_params
          expect(response.body).to match /"total_on_hand":10/
        end

        it 'has 1 image' do
          api_post :create, product_params
          expect(Spree::Product.last.images.count).to eql 1
        end
      end
    end

    context 'when user is not admin' do
      let(:product_params) do
        {
            product: {
                name:              'Foo',
                description:       'Foo Product',
                sku:               'foo001',
                price:             0.0,
                shipping_category: 'Default'
            }
        }
      end

      before do
        stub_authentication!
        controller.stub(:try_spree_current_user).and_return user
      end

      it {
        expect {
          api_post :create, product_params
        }.to change { Spree::Product.count }.by 0
      }

      it 'gets an un-authorize error' do
        api_post :create, product_params
        expect(response.body).to match /You are not authorized to perform that action./
      end
    end
  end

  describe '#update' do
    context 'when use is admin' do
      let(:create_product_params) do
        {
            product: {
                name:              'Foo',
                description:       'Foo Product',
                sku:               'foo001',
                price:             0.0,
                shipping_category: 'Default',
                taxon_ids:         [taxon_1.id, taxon_2.id].join(','),
                stock:             10,
                images:            [
                                       {
                                           base64:       '',
                                           file_name:    'test.png',
                                           content_type: 'image/png'
                                       }
                                   ]
            }
        }
      end
      let(:update_product_params) do
        {
            product: {
                name:              'Foo 2',
                description:       'Foo Product 2',
                sku:               'foo002',
                price:             0.0,
                shipping_category: 'Default',
                taxon_ids:         taxon_2.id.to_s,
                stock:             50,
                images:            [
                                       {
                                           base64:       '',
                                           file_name:    'test.png',
                                           content_type: 'image/png'
                                       },
                                       {
                                           base64:       '',
                                           file_name:    'test2.png',
                                           content_type: 'image/png'
                                       }
                                   ]
            }
        }
      end

      before do
        user.spree_roles.create(name: 'admin')
        stub_authentication!
        controller.stub(:try_spree_current_user).and_return user
        create(:stock_location)
        api_post :create, create_product_params
        update_product_params[:id]                       = Spree::Product.last.id
        update_product_params[:product][:images][0][:id] = Spree::Image.last.id
        api_post :update, update_product_params
      end

      it {
        expect(json_response['name']).to eql 'Foo 2'
      }

      it {
        expect(json_response['taxon_ids']).to eq([taxon_2.id])
      }

      it {
        expect(json_response['total_on_hand']).to eql 50
      }

      it {
        expect(Spree::Product.last.images.count).to eql 2
      }
    end
  end

  describe '#destroy' do
    let!(:product) { create(:product) }
    context 'when user is admin' do
      before do
        user.spree_roles.create(name: 'admin')
        stub_authentication!
        controller.stub(:try_spree_current_user).and_return user
      end

      it {
        expect {
          api_delete :destroy, id: product.to_param
        }.to change { Spree::Product.count }.by -1
      }
    end
  end
end